import React from "react";
import { Container } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { CssBaseline } from "@material-ui/core";
import image from "../assets/22028733.jpeg";
import '../index.css';
export default class Main extends React.Component {
  constructor(props) {
    super(props);
    this.init();
  }
  init() {
    this.state = {
      offsetX: "",
      offsetY: "",
      friction: 1 / 32,
    };
    this._mouseMove = this._mouseMove.bind(this);
  }
  componentDidMount() {
    document.addEventListener("mousemove", this._mouseMove);
  }
  componentWillUnmount() {
    document.removeEventListener("mousemove", this._mouseMove);
  }
  _mouseMove(e) {
    let followX = window.innerWidth / 2 - e.clientX;
    let followY = window.innerHeight / 2 - e.clientY;

    let x = 0,
      y = 0;
    x += (-followX - x) * this.state.friction;
    y += (followY - y) * this.state.friction;
    this.setState({
      offsetX: x,
      offsetY: y,
    });
  }
  render() {
    let offset = {
      transform: `translate(-0%, -50%) perspective(200px)
                  rotateY(${this.state.offsetX}deg)
                  rotateX(${this.state.offsetY}deg)`,
    };

    return (
      <CssBaseline>
        <div
          style={offset}
          className='root'
          >
          <img
            style={{ objectFit: "fill", borderRadius: "210px", textAlign:'center', justifyContent:'center' }}
            src={image}
            alt="me"
          ></img>
          <h1>Dario Sponchiado</h1>

          <p
            style={{
              color: "white",
              width: "50%",
              margin: " 100px ",
              fontSize: 21,
              textAlign: "center",
            }}
          >
            Hi.. i'm dario, i am a develop website(html, css, javascript, php,
            sql, and framework like: react, angular node and more.. ) and
            apps(react-native, flutter, xamarin) for passion, i study at
            ca-foscari university on 1Â° year,
            <br></br> I am open to work send me and email at:{" "}
            <a href="mailto:dario.sponchiado@gmail.com">
              dario.sponchiado@gmail.com
            </a>
          </p>
        </div>
      </CssBaseline>
    );
  }
}
