import { makeStyles } from "@material-ui/core";
import React from "react";
import { ReactComponent as LoaderItem} from '../assets/myText.svg'

const style = makeStyles((theme) => ({
  root: {
    textAlign: "left",
    right:'70%',
    width:'200px',
    fontSize:25,
    zIndex:110000,
    position: "absolute",
  },
}));

const Loader = () => {
  const classes = style();
  return <div className={classes.root}>
     <LoaderItem/>
  </div>;
};
export default Loader;
