import React from "react";
import { Container } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { CssBaseline } from "@material-ui/core";
import image from "../assets/22028733.jpeg";
import "../index.css";
import Loader from "./loader";
import {SnowflakeJs} from "@aicdev/snowflakejs";
export default class Main extends React.Component {
  constructor(props) {
    super(props);
    this.init();
  }
  init() {
    this.state = {
      offsetX: "",
      offsetY: "",
      friction: 1 / 32,
    };
    this._mouseMove = this._mouseMove.bind(this);
  }

  componentDidMount() {
    document.body.style.backgroundColor = "red"
    document.body.style.opacity = '0.9'
    document.addEventListener("mousemove", this._mouseMove);
  }
  componentWillUnmount() {
    document.removeEventListener("mousemove", this._mouseMove);
  }
  _mouseMove(e) {
    let followX = window.innerWidth / 2 - e.clientX;
    let followY = window.innerHeight / 2 - e.clientY;

    let x = 0,
      y = 0;
    x += (-followX - x) * this.state.friction;
    y += (followY - y) * this.state.friction;
    this.setState({
      offsetX: x,
      offsetY: y,
    });
  }

  render() {
    const snowflake = new SnowflakeJs(frames=25, count=50, lifetime=5000, maxSpeed=4, maxSize=15);
